import React, { useState } from "react";
const categories = ["To Do", "In Progress", "Review", "Completed"];
const FiltersPanel = ({ onFilterChange, onSearch }: any) => {
const [selectedCats, setSelectedCats] = useState<string[]>([]);
const [searchText, setSearchText] = useState("");
const toggleCategory = (cat: string) => { let newCats = [...selectedCats]; if (newCats.includes(cat)) newCats = newCats.filter(c => c!==cat); else newCats.push(cat); setSelectedCats(newCats); onFilterChange(newCats); };
return (
<div style={{ display: "flex", gap: 20, marginBottom: 12 }}>
<div><strong>Categories:</strong>{categories.map(cat => <label key={cat} style={{ marginLeft: 5 }}><input type="checkbox" checked={selectedCats.includes(cat)} onChange={()=>toggleCategory(cat)} />{cat}</label>)}</div>
<div><input type="text" placeholder="Search tasks..." value={searchText} onChange={e=>{setSearchText(e.target.value); onSearch(e.target.value);}} /></div>
</div>
);
};
export default FiltersPanel;
