import React, { useContext, useState } from "react";
import { getMonthMatrix, isSameDay } from "../../utils/dateUtils";
import DayCell from "./DayCell";
import { TaskContext } from "../../context/TaskContext";
import { Task } from "../../types/TaskTypes";
import "./Calendar.css";

const weekDays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

interface Props {
  year: number;
  month: number;
  openModal: (start: Date, end: Date, task?: Task) => void;
  categoryFilter?: string[];
  searchText?: string;
}

const Calendar: React.FC<Props> = ({
  year,
  month,
  openModal,
  categoryFilter = [],
  searchText = "",
}) => {
  const days = getMonthMatrix(year, month);
  const { tasks } = useContext(TaskContext);

  // -------------------------------
  // DRAG SELECTION LOGIC
  // -------------------------------
  const [isSelecting, setIsSelecting] = useState(false);
  const [startIndex, setStartIndex] = useState<number | null>(null);
  const [hoverIndex, setHoverIndex] = useState<number | null>(null);

  // NEW: Dragged task index
  const [draggedTaskId, setDraggedTaskId] = useState<number | null>(null);

  const handleMouseDown = (i: number) => {
    if (!days[i]) return;
    setIsSelecting(true);
    setStartIndex(i);
    setHoverIndex(i);
  };
  const handleMouseEnter = (i: number) => {
    if (isSelecting && days[i]) setHoverIndex(i);
  };
  const handleMouseUp = () => {
    if (isSelecting && startIndex !== null && hoverIndex !== null) {
      const s = Math.min(startIndex, hoverIndex);
      const e = Math.max(startIndex, hoverIndex);
      openModal(days[s]!, days[e]!);
    }
    setIsSelecting(false);
    setStartIndex(null);
    setHoverIndex(null);
  };

  const isSelected = (i: number) => {
    if (!isSelecting || startIndex === null || hoverIndex === null) return false;
    return i >= Math.min(startIndex, hoverIndex) && i <= Math.max(startIndex, hoverIndex);
  };

  const today = new Date();

  // Convert date → index
  const dateToIndex = (date: Date) =>
    days.findIndex((d) => d && isSameDay(d, date));

  return (
    <div onMouseUp={handleMouseUp}>
      {/* Week Header */}
      <div className="week-row">
        {weekDays.map((d) => (
          <div key={d} className="week-cell">
            {d}
          </div>
        ))}
      </div>

      {/* GRID WRAPPER */}
      <div className="calendar-grid-wrapper">
        {/* TASK ROWS */}
        <div
          className="task-bar-layer"
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(7, 1fr)",
            position: "relative",
            marginBottom: "6px",
          }}
        >
          {tasks.map((task) => {
            const startIdx = dateToIndex(task.start);
            const endIdx = dateToIndex(task.end);
            if (startIdx === -1 || endIdx === -1) return null;

            const colStart = startIdx + 1;
            const colEnd = endIdx + 2;

            return (
              <div
                key={task.id}
                className="task-bar-long"
                draggable
                onDragStart={() => setDraggedTaskId(task.id)}
                onDragEnd={() => setDraggedTaskId(null)}
                onClick={() => openModal(task.start, task.end, task)}
                style={{
                  gridColumn: `${colStart} / ${colEnd}`,
                  background: task.color || "#2563eb",
                  color: "white",
                  padding: "4px 8px",
                  borderRadius: "6px",
                  whiteSpace: "nowrap",
                  overflow: "hidden",
                  textOverflow: "ellipsis",
                  cursor: "pointer",
                }}
              >
                {/* Show title only if dragged or not dragging */}
                {draggedTaskId === task.id
                  ? task.title
                  : draggedTaskId === null
                  ? task.title
                  : ""}
              </div>
            );
          })}
        </div>

        {/* DAY GRID */}
        <div
          className="calendar-grid"
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(7, 1fr)",
            gap: "2px",
          }}
        >
          {days.map((date, i) =>
            date ? (
            <DayCell
  key={i}
  date={date}
  tasks={tasks}
  openModal={openModal}
  isToday={isSameDay(date, today)}
  onMouseDown={() => handleMouseDown(i)}
  onMouseEnter={() => handleMouseEnter(i)}
  onMouseUp={handleMouseUp}
  draggedTaskId={draggedTaskId} // <-- pass dragged task
/>
            ) : (
              <div key={i} className="empty-cell" />
            )
          )}
        </div>
      </div>
    </div>
  );
};

export default Calendar;
