import React, { createContext, useState, useEffect } from "react";
import { Task } from "../types/TaskTypes";

interface TaskContextProps {
  tasks: Task[];
  addTask: (task: Task) => void;
  updateTask: (task: Task) => void;
  moveTaskToDate: (date: Date) => void;
}

export const TaskContext = createContext<TaskContextProps>({} as TaskContextProps);

export const TaskProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [tasks, setTasks] = useState<Task[]>([]);

  // Load tasks from localStorage on mount
  useEffect(() => {
    const saved = localStorage.getItem("tasks");
    if (saved) setTasks(JSON.parse(saved));
  }, []);

  // Save tasks to localStorage whenever tasks change
  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (task: Task) => setTasks(prev => [...prev, task]);
  const updateTask = (task: Task) => setTasks(prev => prev.map(t => (t.id === task.id ? task : t)));

  const moveTaskToDate = (date: Date) => {
    // implement logic if needed
  };

  return (
    <TaskContext.Provider value={{ tasks, addTask, updateTask, moveTaskToDate }}>
      {children}
    </TaskContext.Provider>
  );
};
