import React, { createContext, useState, useEffect } from "react";
import { Task } from "../types/TaskTypes";

interface TaskContextProps {
  tasks: Task[];
  addTask: (task: Task) => void;
  updateTask: (task: Task) => void;
  moveTaskToDate: (newStart: Date) => void;
  startDraggingTask: (task: Task) => void;
}

export const TaskContext = createContext<TaskContextProps>({
  tasks: [],
  addTask: () => {},
  updateTask: () => {},
  moveTaskToDate: () => {},
  startDraggingTask: () => {},
});

let draggingTask: Task | null = null;

export const TaskProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [tasks, setTasks] = useState<Task[]>([]);

  // ✅ Load from localStorage (convert date strings back to Date objects)
  useEffect(() => {
    const saved = localStorage.getItem("tasks");

    if (saved) {
      const parsed: Task[] = JSON.parse(saved).map((t: any) => ({
        ...t,
        start: new Date(t.start),
        end: new Date(t.end),
      }));
      setTasks(parsed);
    }
  }, []);

  // ✅ Save to localStorage whenever tasks change
  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  // ---------------------
  // CREATE TASK
  // ---------------------
  const addTask = (task: Task) => {
    setTasks((prev) => [...prev, task]);
  };

  // ---------------------
  // UPDATE TASK
  // ---------------------
  const updateTask = (updated: Task) => {
    setTasks((prev) =>
      prev.map((t) => (t.id === updated.id ? updated : t))
    );
  };

  // ---------------------
  // START DRAGGING TASK
  // ---------------------
  const startDraggingTask = (task: Task) => {
    draggingTask = task;
  };

  // ---------------------
  // MOVE TASK TO ANOTHER DAY
  // ---------------------
  const moveTaskToDate = (newStartDate: Date) => {
    if (!draggingTask) return;

    const dayDiff =
      (newStartDate.getTime() - draggingTask.start.getTime()) /
      (1000 * 60 * 60 * 24);

    const newTask: Task = {
      ...draggingTask,
      start: new Date(draggingTask.start.getTime() + dayDiff * 86400000),
      end: new Date(draggingTask.end.getTime() + dayDiff * 86400000),
    };

    updateTask(newTask);

    draggingTask = null;
  };

  return (
    <TaskContext.Provider
      value={{
        tasks,
        addTask,
        updateTask,
        startDraggingTask,
        moveTaskToDate,
      }}
    >
      {children}
    </TaskContext.Provider>
  );
};
