// src/components/Calendar/Calendar.tsx
import React, { useState, useContext } from "react";
import DayCell from "./DayCell";
import { TaskContext } from "../../context/TaskContext";
import { getMonthMatrix } from "../../utils/dateUtils";
import { Task } from "../../types/TaskTypes";

const weekDays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

interface CalendarProps {
  openModal: (start: Date, end: Date, task?: Task) => void;
  categoryFilter: string[];
  searchText: string;
}

const Calendar: React.FC<CalendarProps> = ({ openModal, categoryFilter, searchText }) => {
  const { tasks } = useContext(TaskContext);

const today = new Date();
const year = today.getFullYear();
const month = today.getMonth();
const days = getMonthMatrix(year, month);

  const [isSelecting, setIsSelecting] = useState(false);
  const [startIndex, setStartIndex] = useState<number | null>(null);
  const [hoverIndex, setHoverIndex] = useState<number | null>(null);

  const handleMouseDown = (index: number) => {
    setIsSelecting(true);
    setStartIndex(index);
    setHoverIndex(index);
  };

  const handleMouseEnter = (index: number) => {
    if (!isSelecting) return;
    setHoverIndex(index);
  };

  const handleMouseUp = () => {
    if (!isSelecting || startIndex === null || hoverIndex === null) return;
    setIsSelecting(false);

    const start = Math.min(startIndex, hoverIndex);
    const end = Math.max(startIndex, hoverIndex);

    const startDate = days[start]!;
    const endDate = days[end]!;

    if (startDate && endDate) {
      openModal(startDate, endDate); // open modal for new task
    }

    setStartIndex(null);
    setHoverIndex(null);
  };

  const isSelected = (index: number) => {
    if (!isSelecting || startIndex === null || hoverIndex === null) return false;
    const min = Math.min(startIndex, hoverIndex);
    const max = Math.max(startIndex, hoverIndex);
    return index >= min && index <= max;
  };

  return (
    <div onMouseUp={handleMouseUp}>
      {/* Week header */}
      <div style={{ display: "flex", marginBottom: 4 }}>
        {weekDays.map((d) => (
          <div key={d} style={{ flex: 1, textAlign: "center", fontWeight: "bold" }}>
            {d}
          </div>
        ))}
      </div>

      {/* Calendar Grid */}
      <div style={{ display: "grid", gridTemplateColumns: "repeat(7, 1fr)", gap: 2 }}>
        {days.map((date, i) => {
          const dayTasks = date
            ? tasks.filter(
                (t) =>
                  t.start.toDateString() === date.toDateString() &&
                  (!categoryFilter.length || categoryFilter.includes(t.category || "")) &&
                  t.title.toLowerCase().includes(searchText.toLowerCase())
              )
            : [];

          const isToday = date ? date.toDateString() === today.toDateString() : false;

          return (
            <DayCell
              key={i}
              date={date}
              tasks={dayTasks}
              isSelected={isSelected(i)}
              onMouseDown={() => handleMouseDown(i)}
              onMouseEnter={() => handleMouseEnter(i)}
              onMouseUp={handleMouseUp}
              openModal={openModal}
              isToday={isToday} // highlight current date
            />
          );
        })}
      </div>
    </div>
  );
};

export default Calendar;
