import React from "react";
import { Task } from "../../types/TaskTypes";
import { isSameDay } from "../../utils/dateUtils";

interface DayCellProps {
  date: Date;
  tasks: Task[];
  isToday?: boolean;
  openModal: (start: Date, end: Date, task?: Task) => void;
  onMouseDown?: () => void;
  onMouseEnter?: () => void;
  onMouseUp?: () => void;
  draggedTaskId?: number | null; // New prop
}

const DayCell: React.FC<DayCellProps> = ({
  date,
  tasks,
  isToday = false,
  openModal,
  onMouseDown,
  onMouseEnter,
  onMouseUp,
  draggedTaskId,
}) => {
  return (
    <div
      className={`day-cell border border-gray-300 min-h-[80px] relative ${
        isToday ? "bg-yellow-100" : ""
      }`}
      onMouseDown={onMouseDown}
      onMouseEnter={onMouseEnter}
      onMouseUp={onMouseUp}
    >
      {tasks.map((task) => {
        const isDragged = draggedTaskId === task.id;
        const isTaskInCell = isSameDay(task.start, date); // show only in start cell

        return (
          <div
            key={task.id}
            className={`task absolute top-1 left-1 right-1 p-1 rounded text-white cursor-pointer ${
              isDragged ? "bg-blue-600 z-20" : "bg-blue-400"
            }`}
            style={{
              whiteSpace: "nowrap",
              overflow: "hidden",
              textOverflow: "ellipsis",
            }}
            onClick={() => openModal(task.start, task.end, task)}
          >
            {isDragged || isTaskInCell ? task.title : ""}
          </div>
        );
      })}
    </div>
  );
};

export default DayCell;
