import React from "react";
import TaskBar from "./TaskBar";
import { TaskContext } from "../../context/TaskContext";

interface DayCellProps {
  date: Date | null;
  tasks: any[];
  draggedId: string | null;
  onDragStart: (taskId: string) => void;
}

const DayCell: React.FC<DayCellProps> = ({
  date,
  tasks,
  draggedId,
  onDragStart,
}) => {
  const { moveTaskToDate } = React.useContext(TaskContext);

  const handleDrop = () => {
    if (!draggedId || !date) return; 
    moveTaskToDate(date, draggedId); // FIXED: two arguments
  };

  const isToday =
    date && new Date().toDateString() === date.toDateString();

  return (
    <div
      className="relative border p-1 flex flex-col gap-1 overflow-hidden rounded-md"
      onDragOver={(e) => e.preventDefault()}
      onDrop={handleDrop}
    >
      {/* DATE */}
      <div
        className={`text-xs font-semibold mb-1 ${
          isToday ? "text-blue-600" : "text-gray-700"
        }`}
      >
        {date ? date.getDate() : ""}
      </div>

      {/* TASK LIST */}
      <div className="flex flex-col gap-1 overflow-y-auto max-h-[85px] pr-1">
        {tasks.map((task) => (
          <TaskBar key={task.id} task={task} />
        ))}
      </div>
    </div>
  );
};

export default DayCell;
