import React, { createContext, useState, useEffect } from "react";
import { Task } from "../types/TaskTypes";

interface TaskContextProps {
  tasks: Task[];
  addTask: (task: Task) => void;
  updateTask: (task: Task) => void;
  moveTaskToDate: (date: Date) => void;
}

export const TaskContext = createContext<TaskContextProps>({
  tasks: [],
  addTask: () => {},
  updateTask: () => {},
  moveTaskToDate: () => {},
});

export const TaskProvider = ({ children }: { children: React.ReactNode }) => {
  const [tasks, setTasks] = useState<Task[]>([]);

  // --------------------------
  // ✅ Load from localStorage
  // --------------------------
  useEffect(() => {
    const saved = localStorage.getItem("tasks");
    if (saved) {
      try {
        const parsed: Task[] = JSON.parse(saved);

        // Convert dates back into Date objects
        const restored = parsed.map((t) => ({
          ...t,
          start: new Date(t.start),
          end: new Date(t.end),
        }));

        setTasks(restored);
      } catch (e) {
        console.error("❌ FAILED TO LOAD LOCAL STORAGE:", e);
        setTasks([]);
      }
    }
  }, []);

  // --------------------------
  // ✅ Save to localStorage
  // --------------------------
  useEffect(() => {
    if (tasks.length > 0) {
      localStorage.setItem("tasks", JSON.stringify(tasks));
    }
  }, [tasks]);

  // --------------------------
  // ADD TASK
  // --------------------------
  const addTask = (task: Task) => {
    setTasks((prev) => [...prev, task]);
  };

  // --------------------------
  // UPDATE TASK
  // --------------------------
  const updateTask = (task: Task) => {
    setTasks((prev) => prev.map((t) => (t.id === task.id ? task : t)));
  };

  // --------------------------
  // MOVE TASK BY DRAGGING
  // --------------------------
  const moveTaskToDate = (newDate: Date) => {
    const draggedId = localStorage.getItem("drag-task-id");
    if (!draggedId) return;

    setTasks((prev) =>
      prev.map((t) =>
        t.id === draggedId
          ? {
              ...t,
              start: newDate,
              end: newDate,
            }
          : t
      )
    );

    localStorage.removeItem("drag-task-id");
  };

  return (
    <TaskContext.Provider
      value={{
        tasks,
        addTask,
        updateTask,
        moveTaskToDate,
      }}
    >
      {children}
    </TaskContext.Provider>
  );
};
