import React, { useState, useContext, useEffect } from "react";
import { Task } from "../../types/TaskTypes";
import { TaskContext } from "../../context/TaskContext";

const categories = ["To Do", "In Progress", "Review", "Completed"];

interface TaskModalProps {
  isOpen: boolean;
  editingTask?: Task | null;
  defaultStart: Date | null;
  defaultEnd: Date | null;
  onClose: () => void;
}

const TaskModal: React.FC<TaskModalProps> = ({ isOpen, editingTask, defaultStart, defaultEnd, onClose }) => {
  const { addTask, updateTask } = useContext(TaskContext);
  const [title, setTitle] = useState<string>(editingTask?.title || "");
  const [category, setCategory] = useState<string>(editingTask?.category || "To Do");

useEffect(() => {
  if (editingTask) {
    setTitle(editingTask.title);
    setCategory(editingTask.category || "To Do");
  } else {
    setTitle("");       // CLEAR modal when creating new task
    setCategory("To Do");
  }
}, [editingTask, defaultStart, defaultEnd]);

  const handleSave = () => {
    if (!title || !defaultStart || !defaultEnd) return;

    const task: Task = {
      id: editingTask?.id || Date.now().toString(),
      title,
      category,
      start: defaultStart,
      end: defaultEnd,
    };

    if (editingTask) updateTask(task);
    else addTask(task);

    onClose();
  };

  if (!isOpen) return null;

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: "rgba(0,0,0,0.3)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 1000,
      }}
    >
      <div style={{ background: "#fff", padding: 20, borderRadius: 8, minWidth: 300 }}>
        <h3>{editingTask ? "Edit Task" : "New Task"}</h3>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Task title"
          style={{ width: "100%", marginBottom: 10 }}
        />
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          style={{ width: "100%", marginBottom: 10 }}
        >
          {categories.map((c) => (
            <option key={c} value={c}>
              {c}
            </option>
          ))}
        </select>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <button onClick={onClose}>Cancel</button>
          <button onClick={handleSave}>{editingTask ? "Update" : "Add"}</button>
        </div>
      </div>
    </div>
  );
};

export default TaskModal;
