import React, { useContext } from "react";
import { Task } from "../../types/TaskTypes";
import { TaskContext } from "../../context/TaskContext";

// Add `isToday` to props
interface DayCellProps {
  date: Date | null;
  tasks: Task[];
  isSelected?: boolean;
  onMouseDown?: () => void;
  onMouseEnter?: () => void;
  onMouseUp?: () => void;
  openModal?: (start: Date, end: Date, task?: Task) => void;
  isToday?: boolean; // ✅ added this
}

const DayCell: React.FC<DayCellProps> = ({
  date,
  tasks,
  isSelected,
  onMouseDown,
  onMouseEnter,
  onMouseUp,
  openModal,
  isToday,
}) => {
  const { moveTaskToDate } = useContext(TaskContext);

  const handleDrop = () => {
    if (date) moveTaskToDate(date);
  };

  return (
    <div
      className={`day-cell ${isSelected ? "selected-cell" : ""}`}
      onMouseDown={onMouseDown}
      onMouseEnter={onMouseEnter}
      onMouseUp={onMouseUp}
      onDragOver={(e) => e.preventDefault()}
      onDrop={handleDrop}
      style={{
        border: "1px solid #ddd",
        minHeight: 100,
        padding: 6,
        background: isToday ? "royalblue" : "#fff", // ✅ highlight today
        color: isToday ? "#fff" : "#000",
      }}
    >
      {date && <div style={{ fontWeight: "bold", marginBottom: 4 }}>{date.getDate()}</div>}
      {tasks.map((t) => (
        <div
          key={t.id}
          style={{
            background: t.color || "lightgreen",
            borderRadius: 4,
            padding: "2px 4px",
            marginBottom: 2,
            cursor: "pointer",
          }}
          onClick={() => openModal && openModal(t.start, t.end, t)}
        >
          {t.title}
        </div>
      ))}
    </div>
  );
};

export default DayCell;
