import React, { createContext, useEffect, useState } from "react";
import { Task } from "../types/TaskTypes";

interface TaskContextProps {
  tasks: Task[];
  addTask: (task: Task) => void;
  updateTask: (task: Task) => void;
  moveTaskToDate: (date: Date, taskId?: string) => void;
}

export const TaskContext = createContext<TaskContextProps>({
  tasks: [],
  addTask: () => {},
  updateTask: () => {},
  moveTaskToDate: () => {},
});

export const TaskProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [tasks, setTasks] = useState<Task[]>([]);

  // Load on mount
  useEffect(() => {
    const saved = localStorage.getItem("mp_tasks_v1");
    if (!saved) return;
    try {
      const parsed = JSON.parse(saved) as any[];
      const restored: Task[] = parsed.map((t) => ({
        ...t,
        start: new Date(t.start),
        end: new Date(t.end),
      }));
      setTasks(restored);
    } catch (e) {
      console.error("Failed to parse tasks from localStorage", e);
      setTasks([]);
    }
  }, []);

  // Save whenever tasks change
  useEffect(() => {
    try {
      localStorage.setItem("mp_tasks_v1", JSON.stringify(tasks));
    } catch (e) {
      console.error("Failed to save tasks to localStorage", e);
    }
  }, [tasks]);

  const addTask = (task: Task) => setTasks((p) => [...p, task]);

  const updateTask = (task: Task) => setTasks((p) => p.map((t) => (t.id === task.id ? task : t)));

  // Move a task (drag-drop). If taskId provided, use it; otherwise try localStorage drag id.
  const moveTaskToDate = (date: Date, taskId?: string) => {
    const id = taskId || localStorage.getItem("drag-task-id");
    if (!id) return;
    setTasks((p) =>
      p.map((t) => {
        if (t.id !== id) return t;
        // preserve duration (days difference)
        const durationDays = Math.round((t.end.getTime() - t.start.getTime()) / (1000 * 60 * 60 * 24));
        const newStart = new Date(date);
        const newEnd = new Date(date);
        newEnd.setDate(newStart.getDate() + durationDays);
        return { ...t, start: newStart, end: newEnd };
      })
    );
    localStorage.removeItem("drag-task-id");
  };

  return (
    <TaskContext.Provider value={{ tasks, addTask, updateTask, moveTaskToDate }}>
      {children}
    </TaskContext.Provider>
  );
};
