import React from "react";
import { Task } from "../../types/TaskTypes";
import TaskBar from "./TaskBar";

interface DayCellProps {
    date: Date;
    tasks: Task[];
    openModal: (start: Date, end: Date, task?: Task) => void;
    isToday: boolean;
}

const DayCell: React.FC<DayCellProps> = ({ date, tasks, openModal, isToday }) => {
    const dayNumber = date.getDate();

    const handleClick = () => {
        // Open modal for creating a new task on this specific date
        openModal(date, date, undefined);
    };

    return (
        <div
            className={`day-cell border p-1 flex flex-col cursor-pointer transition-all 
                ${isToday ? "bg-blue-100 border-blue-500" : "bg-white"}`}
            onClick={handleClick}
            style={{
                minHeight: 85,
                overflow: "hidden",
                borderRadius: 4,
            }}
        >
            {/* Day number */}
            <div
                className={`text-xs mb-1 font-semibold 
                    ${isToday ? "text-blue-700" : "text-gray-700"}`}
            >
                {dayNumber}
            </div>

            {/* Tasks */}
            <div className="flex flex-col gap-[2px] overflow-hidden">
                {tasks.map((task) => (
                    <TaskBar key={task.id} task={task} onClick={() => openModal(date, date, task)} />
                ))}
            </div>
        </div>
    );
};

export default DayCell;
