// src/context/TaskContext.tsx
import React, { createContext, useState, useEffect } from "react";
import { Task } from "../types/TaskTypes";

interface TaskContextType {
  tasks: Task[];
  addTask: (task: Task) => void;
  updateTask: (task: Task) => void;
  startDraggingTask: (id: string) => void;
  moveTaskToDate: (newDate: Date, taskId?: string) => void;
}

export const TaskContext = createContext<TaskContextType>({
  tasks: [],
  addTask: () => {},
  updateTask: () => {},
  startDraggingTask: () => {},
  moveTaskToDate: () => {},
});

export const TaskProvider = ({ children }: { children: React.ReactNode }) => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [dragTaskId, setDragTaskId] = useState<string | null>(null);

  // ---------------------------
  // LOAD TASKS FROM LOCAL STORAGE
  // ---------------------------
  useEffect(() => {
    try {
      const saved = localStorage.getItem("task-list");
      if (saved) {
        const parsed = JSON.parse(saved);
        if (Array.isArray(parsed)) {
          setTasks(parsed);
        }
      }
    } catch (err) {
      console.error("Error reading tasks:", err);
    }
  }, []);

  // ---------------------------
  // SAVE TASKS TO LOCAL STORAGE
  // ---------------------------
  useEffect(() => {
    try {
      localStorage.setItem("task-list", JSON.stringify(tasks));
    } catch (err) {
      console.error("Error saving tasks:", err);
    }
  }, [tasks]);

  const addTask = (task: Task) => {
    setTasks((prev) => [...prev, task]);
  };

  const updateTask = (task: Task) => {
    setTasks((prev) =>
      prev.map((t) => (t.id === task.id ? task : t))
    );
  };

  const startDraggingTask = (id: string) => {
    setDragTaskId(id);
    localStorage.setItem("drag-task-id", id);
  };

  const moveTaskToDate = (newDate: Date, taskId?: string) => {
    const id = taskId || dragTaskId;
    if (!id) return;

    setTasks((prev) =>
      prev.map((t) =>
        t.id === id
          ? { ...t, start: newDate, end: newDate }
          : t
      )
    );

    localStorage.removeItem("drag-task-id");
    setDragTaskId(null);
  };

  return (
    <TaskContext.Provider
      value={{
        tasks,
        addTask,
        updateTask,
        startDraggingTask,
        moveTaskToDate,
      }}
    >
      {children}
    </TaskContext.Provider>
  );
};
