import React, { useState } from "react";
import Calendar from "./components/Calendar/Calendar";
import TaskModal from "./components/TaskModal/TaskModal";
import FiltersPanel from "./components/Filters/FiltersPanel";
import { Task } from "./types/TaskTypes";


function App() {
const [modalOpen, setModalOpen] = useState(false);
const [editingTask, setEditingTask] = useState<Task | null>(null);
const [rangeStart, setRangeStart] = useState<Date | null>(null);
const [rangeEnd, setRangeEnd] = useState<Date | null>(null);


const [categoryFilter, setCategoryFilter] = useState<string[]>([]);
const [searchText, setSearchText] = useState("");


const openModal = (start: Date, end: Date, task?: Task) => { setRangeStart(start); setRangeEnd(end); setEditingTask(task||null); setModalOpen(true); };


return (
<div style={{ padding: 20 }}>
<FiltersPanel onFilterChange={setCategoryFilter} onSearch={setSearchText} />
<TaskModal isOpen={modalOpen} editingTask={editingTask} defaultStart={rangeStart} defaultEnd={rangeEnd} onClose={()=>setModalOpen(false)} />
</div>
);
}
export default App;
