import React, { createContext, useEffect, useState } from "react";
import { Task } from "../types/TaskTypes";


type TaskContextType = {
tasks: Task[];
addTask: (task: Task) => void;
updateTask: (task: Task) => void;
startDraggingTask: (id: string) => void;
moveTaskToDate: (newDate: Date) => void;
};


export const TaskContext = createContext<TaskContextType>({} as TaskContextType);


export const TaskProvider = ({ children }: { children: React.ReactNode }) => {
const [tasks, setTasks] = useState<Task[]>([]);
const [dragTaskId, setDragTaskId] = useState<string | null>(null);


const addTask = (task: Task) => setTasks(prev => [...prev, task]);
const updateTask = (updated: Task) => setTasks(prev => prev.map(t => t.id === updated.id ? updated : t));


const startDraggingTask = (id: string) => setDragTaskId(id);


const moveTaskToDate = (newDate: Date) => {
if (!dragTaskId) return;
setTasks(prev => prev.map(t => t.id === dragTaskId ? { ...t, start: newDate, end: newDate } : t));
setDragTaskId(null);
};

useEffect(() => {
  const savedTasks = localStorage.getItem("tasks");
  if (savedTasks) setTasks(JSON.parse(savedTasks, (key, value) => {
    if (key === "start" || key === "end") return new Date(value);
    return value;
  }));
}, []);

useEffect(() => {
  localStorage.setItem("tasks", JSON.stringify(tasks));
}, [tasks]);

return (
<TaskContext.Provider value={{ tasks, addTask, updateTask, startDraggingTask, moveTaskToDate }}>
{children}
</TaskContext.Provider>
);
};
