// src/utils/dateUtils.ts
export const getMonthMatrix = (year: number, month: number): (Date | null)[] => {
  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const matrix: (Date | null)[] = [];

  // Fill empty days before first day of month
  for (let i = 0; i < firstDay.getDay(); i++) {
    matrix.push(null);
  }

  // Fill the actual days
  for (let d = 1; d <= lastDay.getDate(); d++) {
    matrix.push(new Date(year, month, d));
  }

  // Fill trailing empty days to complete last week
  while (matrix.length % 7 !== 0) {
    matrix.push(null);
  }

  return matrix;
};
