import React, { createContext, useContext, useState } from "react";
import { Task } from "../types/types";

type TaskContextType = {
  tasks: Task[];
  addTask: (task: Task) => void;
  updateTask: (task: Task) => void;
  deleteTask: (id: string) => void;

  startDragging: (task: Task) => void;
  stopDragging: () => void;
  moveTaskToDate: (newDate: Date, draggedTaskId?: string) => void;

  saveTasks: () => void;
  loadTasks: () => void;
};

const TaskContext = createContext<TaskContextType | undefined>(undefined);

export const useTaskContext = () => {
  const context = useContext(TaskContext);
  if (!context) throw new Error("useTaskContext must be inside TaskProvider");
  return context;
};

let draggingTask: Task | null = null;

export const TaskProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [tasks, setTasks] = useState<Task[]>(() => {
    const saved = localStorage.getItem("tasks");
    return saved ? JSON.parse(saved) : [];
  });

  const addTask = (task: Task) => {
    const newTasks = [...tasks, task];
    setTasks(newTasks);
    localStorage.setItem("tasks", JSON.stringify(newTasks));
  };

  const updateTask = (updated: Task) => {
    const newTasks = tasks.map((t) => (t.id === updated.id ? updated : t));
    setTasks(newTasks);
    localStorage.setItem("tasks", JSON.stringify(newTasks));
  };

  const deleteTask = (id: string) => {
    const newTasks = tasks.filter((t) => t.id !== id);
    setTasks(newTasks);
    localStorage.setItem("tasks", JSON.stringify(newTasks));
  };

  const startDragging = (task: Task) => {
    draggingTask = task;
  };

  const stopDragging = () => {
    draggingTask = null;
  };

  // ✅ FIXED: Now supports dragging with (date, draggedId)
  const moveTaskToDate = (newDate: Date, draggedTaskId?: string) => {
    let taskToMove = draggingTask;

    // If context lost the draggingTask, fallback using ID
    if (!taskToMove && draggedTaskId) {
      taskToMove = tasks.find((t) => t.id === draggedTaskId) || null;
    }

    if (!taskToMove) return;

    const dayDiff =
      (newDate.getTime() - taskToMove.start.getTime()) /
      (1000 * 60 * 60 * 24);

    const updatedTask: Task = {
      ...taskToMove,
      start: new Date(taskToMove.start.getTime() + dayDiff * 86400000),
      end: new Date(taskToMove.end.getTime() + dayDiff * 86400000),
    };

    updateTask(updatedTask);

    draggingTask = null;
  };

  const saveTasks = () => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  };

  const loadTasks = () => {
    const saved = localStorage.getItem("tasks");
    if (saved) setTasks(JSON.parse(saved));
  };

  return (
    <TaskContext.Provider
      value={{
        tasks,
        addTask,
        updateTask,
        deleteTask,
        startDragging,
        stopDragging,
        moveTaskToDate,
        saveTasks,
        loadTasks,
      }}
    >
      {children}
    </TaskContext.Provider>
  );
};
